UPDATE `script_texts` SET `content_loc6`='¡Ayúdeme! ¡Quíteme estas cosas de encima y podré hacer mi trabajo!' WHERE `content_default`='Help!!! Get these things off me so I can get my work done!';
UPDATE `script_texts` SET `content_loc6`='¡Ya casi está! ¡Sólo un poco más!' WHERE `content_default`='Almost done! Just a little longer!';
UPDATE `script_texts` SET `content_loc6`='¡Lo conseguí! ¡He captado la energía de la piedra en los gafas! ¡Salgamos de aquí! ' WHERE `content_default`='I\'ve done it! I have harnessed the power of the stone into the goggles! Let\'s get out of here!';
UPDATE `script_texts` SET `content_loc6`='¡Uf! Me alegra haber regresado de esa espeluznante cueva. ' WHERE `content_default`='Phew! Glad to be back from that creepy cave.';
