UPDATE `script_texts` SET `content_loc6`='El Doctor Draxlegauge te dará instrucciones adicionales, $N. ¡Muchas gracias por tu ayuda! ' WHERE `content_default`='Doctor Draxlegauge will give you further instructions, $N. Many thanks for your help!';
UPDATE `script_texts` SET `content_loc6`='¡Es hora de enseñarte una lección de modales, $Gmuchachito:muchachita;!' WHERE `content_default`='Time to teach you a lesson in manners, little $Gboy:girl;!';
UPDATE `script_texts` SET `content_loc6`='Ahora voy a contarte hasta \"3\" para que salgas de aquí antes de que te eche los sabuesos.' WHERE `content_default`='Now I\'m gonna give you to the count of \'3\' to get out of here before I sick the dogs on you.';
