UPDATE `script_texts` SET `content_loc6`='¡Nadie reta a los nómadas Deambuladores - y menos los pollos robot! ¡AL ATAQUE!' WHERE `content_default`='No one challanges the Wastewander nomads - not even robotic chickens! ATTACK!';
UPDATE `script_texts` SET `content_loc6`='¡Ocultando sistemas en linea! ¡Cock! ¡Acoplando camuflaje para transporte a Bahía del Botín!' WHERE `content_default`='Cloaking systems online! CLUCK! Engaging cloak for transport to Booty Bay!';
UPDATE `script_texts` SET `content_loc6`='¡Para casa! ¡Quédate cerca de mí, pase lo que pase! ¡Allí tengo mi arma y munición!' WHERE `content_default`='To the house! Stay close to me, no matter what! I have my gun and ammo there!';
