UPDATE `script_texts` SET `content_loc6`='¡Ahí está el desfibrilador robado! ¡Detenlo o Lugwizzle obtendrá nuestros secretos!' WHERE `content_default`='There\'s the stolen shredder! Stop it or Lugwizzle will have our hides!';
UPDATE `script_texts` SET `content_loc6`='Parece que estamos fuera del bosque, ¿eh? Me pregunto que hace esta...' WHERE `content_default`='Looks like we\'re out of woods, eh? Wonder what this does...';
